/**
 * Audit logging helper for authentication attempts
 */
export function logAuthAttempt(
    type: "signin" | "signup",
    email: string,
    success: boolean,
    error?: string,
): void {
    console.log(
        `[BETTER_AUTH] ${type.toUpperCase()} - ${email} - ${success ? "SUCCESS" : "FAILED"}${error ? ` - ${error}` : ""}`,
    );
}
